INSERT INTO cscart_conf_group_descriptions (`group_id`, `lang_code`, `configurator_group_name`, `full_description`) VALUES ('34', 'EN', 'Modems', '<span style=\"font-weight: bold; font-style: italic;\">Description</span><br />Le modem U.S. Robotics 56K Message Modem associe des fonctions de messagerie téléphonique à la technologie Internet avancée V.92 pour que vous puissiez tirer parti au maximum de votre connexion. La messagerie vocale intégrée est capable de stocker jusqu\'à 20 minutes de messages téléphoniques. <span style=\"font-style: italic;\">Quick Connect </span>minimise les délais de connexion pour que vous accédiez plus rapidement à l\'Internet. La fonction Modem on Hold vous permet d\'afficher les appels entrants et de prendre les plus importants, tout en continuant à surfer sur le Web? - vous n\'avez pas besoin d\'une seconde ligne téléphonique! V.PCM Upstream permet des chargements jusqu\'à 50 % plus rapides - parfait pour l\'envoi d\'e-mails avec pièces jointes.');
INSERT INTO cscart_conf_group_descriptions (`group_id`, `lang_code`, `configurator_group_name`, `full_description`) VALUES ('35', 'EN', 'Hard Disk Drives', '<span style=\"font-weight: bold; font-style: italic;\">Description<br /></span>Meet the newest noise prevention hard disk drive cooler from Zalman. Using heatpipe technology, the ZM2HC2 cools the hard disk drive for more stable operation, without adding the noise of additional cooling fans to your PC. Its special mounts also reduce hard disk drive noise and vibration.');
