INSERT INTO cscart_product_descriptions (`product_id`, `lang_code`, `product`, `shortname`, `short_description`, `full_description`, `meta_keywords`, `meta_description`, `search_words`, `page_title`, `age_warning_message`) VALUES ('29759', 'EN', 'eVGA NVIDIA e-GeForce FX 5500 256 MB, 128 bit DDR AGP 8X VGA/DVI/TV Video Card (256-A8-N313-LX)', '', '', '<b>Product Description</b><br />The NVIDIA GeForce FX 5500 Chip, is a GPU that will unleash stunning 3D worlds for the mainstream. Powered by the same advanced technology and features as the rest of the FX family, the e-GeForce FX 5500 delivers studio-quality color and amazing cinematic gaming effects. With 256MB of video memory and the latest NVIDIA Technology, such as the CineFX Engine, and High-Precision Graphics, the e-GeForce FX 5500 offers a lot in a small package.<br /><b>Technical Details</b><br /><br /><ul><li>CineFX Engine</li><li>Intellisample</li><li>Multi-display Option</li><li>Digital Vibrance Control</li><li>MPEG2 Decode Acceleration</li><br /></ul>', '', '', '', '', '');
